def maxFrequency(frequencyDict):
    maxFrequency = 0
    for f in frequencyDict.values():
        maxFrequency = max(maxFrequency, f)
    return maxFrequency

def characterReplacement(s, k):
    left = 0
    right = 0
    maxLength = 0
    length = 0

    frequencyDict = dict()
    frequencyDict[s[left]] = 1

    while right < len(s):
        length = right - left + 1

        if (length - maxFrequency(frequencyDict)) <= k:
            right += 1

            if right < len(s):
                if s[right] not in frequencyDict.keys():
                    frequencyDict[s[right]] = 1
                else: frequencyDict[s[right]] += 1

            maxLength = max(maxLength, length)
        else:
            frequencyDict[s[left]] -= 1
            left += 1

    return maxLength


