# O(n x k) solution :(, we need sliding window
def characterReplacementOld(s, k):
    letters = dict()

    for i in range(len(s)):
        if s[i] not in letters:
            letters[s[i]] = []
        
        letters[s[i]].append(i)
    
    maxLength = 0

    for letter, indices in letters.items():
        k = min(k, len(s) - len(indices))

        if len(s) == len(indices): return len(indices)

        for i in indices:
            subK = k
            length = 0
            subI = i + 1

            if i > 0:
                if s[i - 1] == letter:
                    continue

            while(subK > -1):
                length += 1

                if(subI == len(s)):
                    length += subK
                    break    
                if s[subI] != letter:
                    subK -= 1

                subI += 1
            
            maxLength = max(maxLength, length)
    
    return maxLength
                
        

